package xyz.nyist.leetcode.begin000;

/**
 * @Author: silence
 * 给你一个字符串 S、一个字符串 T，请在字符串 S 里面找出：包含 T 所有字符的最小子串。
 * <p>
 * 示例：
 * <p>
 * 输入: S = "ADOBECODEBANC", T = "ABC"
 * 输出: "BANC"
 * <p>
 * 来源：力扣（LeetCode）
 * 链接：https://leetcode-cn.com/problems/minimum-window-substring
 * 著作权归领扣网络所有。商业转载请联系官方授权，非商业转载请注明出处。
 * @Date:Create：in 2020/5/24 17:19
 */
public class Solution76 {

    public static void main(String[] args) {
        long start = System.currentTimeMillis();
        //System.out.println(minWindow("", ""));
        System.out.println(minWindow("ADOBECODEBANC", "ABC"));
        System.out.println(System.currentTimeMillis() - start);


        Solution76 solution76 = new Solution76();
        System.out.println(solution76.minWindow1("acbbaca",
                "aba"));
    }


    public static String minWindow(String s, String t) {
        if (s.length() < t.length()) {
            return "";
        }
        if (s.equals(t) || (t.length() == 1 && s.contains(t))) {
            return t;
        }
        char[] sa = s.toCharArray(), ta = t.toCharArray();
        int[] exit = new int[128], need = new int[128];
        for (char c : ta) {
            need[c]++;
        }
        int left = 0, right = 0, begin = 0, min = s.length() + 1, des = 0;
        while (right < s.length()) {
            if (need[sa[right]] == 0) {
                right++;
                continue;
            }
            if (exit[sa[right]] < need[sa[right]]) {
                des++;
            }
            exit[sa[right]]++;
            right++;
            while (des == t.length()) {
                if (min > right - left) {
                    min = right - left;
                    begin = left;
                }
                if (need[sa[left]] == 0) {
                    left++;
                    continue;
                }
                if (exit[sa[left]] == need[sa[left]]) {
                    des--;
                }
                exit[sa[left]]--;
                left++;
            }

        }
        if (begin + min > s.length()) {
            return "";
        }
        return s.substring(begin, begin + min);
    }


    public String minWindow1(String s, String t) {
        if (s.length() < t.length()) {
            return "";
        }
        if (s.equals(t) || (t.length() == 1 && s.contains(t))) {
            return t;
        }


        char[] sChars = s.toCharArray();

        int[] need = new int[128];
        int[] windows = new int[128];

        int needLen = 0;
        for (char c : t.toCharArray()) {
            if (need[c]++ == 0) {
                needLen++;
            }
        }

        int valid = 0;

        int min = Integer.MAX_VALUE, start = -1;


        int left = 0, right = 0;
        while (right < sChars.length) {
            char c = sChars[right++];

            if (need[c] > 0) {
                windows[c]++;
                if (need[c] == windows[c]) {
                    valid++;
                }
            }

            while (valid == needLen) {

                if (right - left < min) {
                    min = right - left;
                    start = left;
                }

                char k = sChars[left];
                windows[k]--;

                if (need[k] != 0 && windows[k] < need[k]) {
                    valid--;
                }
                left++;

            }


        }
        if (start != -1) {
            return s.substring(start, start + min);
        }

        return "";
    }
}








